import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';

const AdminPage = () => {
  const socketRef = useRef(null);
  const [gameState, setGameState] = useState({
    lanes: [
      { id: 1, baseHealth: 100, enemies: [], bullets: [], isGameOver: false },
      { id: 2, baseHealth: 100, enemies: [], bullets: [], isGameOver: false },
      { id: 3, baseHealth: 100, enemies: [], bullets: [], isGameOver: false },
      { id: 4, baseHealth: 100, enemies: [], bullets: [], isGameOver: false }
    ],
    waveSystem: {
      isActive: false,
      isPaused: false,
      currentWave: 0,
      timeRemaining: 20
    },
    globalGameOver: false,
    winner: null,
    totalScore: 0
  });
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    socketRef.current = io('http://localhost:5000');
    
    socketRef.current.on('connect', () => {
      setIsConnected(true);
      console.log('Admin conectado al servidor');
    });
    
    socketRef.current.on('disconnect', () => {
      setIsConnected(false);
      console.log('Admin desconectado del servidor');
    });
    
    socketRef.current.on('gameState', (newGameState) => {
      setGameState(newGameState);
    });
    
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  const spawnEnemy = (laneId) => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('spawnEnemy', laneId);
    }
  };

  const shootBullet = (laneId) => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('shootBullet', laneId);
    }
  };

  const shootAllBullets = () => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('shootAllBullets');
    }
  };

  const startWaves = () => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('startWaves');
    }
  };

  const pauseWaves = () => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('pauseWaves');
    }
  };

  const stopWaves = () => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('stopWaves');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="admin-container-v2">
      <div className="admin-panel-v2">
        <div className="admin-header">
          <h1 className="admin-title-v2">üéÆ Centro de Control</h1>
          <div className="admin-subtitle">Sistema de Oleadas Tower Defense</div>
        </div>
        
        {/* Estado de conexi√≥n */}
        <div className={`connection-status-v2 ${isConnected ? 'connected' : 'disconnected'}`}>
          <div className="connection-indicator">
            {isConnected ? 'üü¢' : 'üî¥'}
          </div>
          <span>{isConnected ? 'Conectado al servidor' : 'Desconectado del servidor'}</span>
        </div>

        {/* Panel de estado de oleadas */}
        <div className="wave-status-panel">
          <div className="wave-info">
            <div className="wave-counter">
              <h3>üåä Oleada {gameState.waveSystem.currentWave}</h3>
              <div className="wave-timer">
                ‚è±Ô∏è {formatTime(gameState.waveSystem.timeRemaining)}
              </div>
            </div>
            <div className="game-stats">
              <div className="stat-item">
                <span className="stat-label">üéØ Puntuaci√≥n:</span>
                <span className="stat-value">{gameState.totalScore}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">‚öîÔ∏è Estado:</span>
                <span className={`stat-value status-${
                  gameState.waveSystem.isActive ? 
                    (gameState.waveSystem.isPaused ? 'paused' : 'active') : 'stopped'
                }`}>
                  {gameState.waveSystem.isActive ? 
                    (gameState.waveSystem.isPaused ? '‚è∏Ô∏è Pausado' : '‚ñ∂Ô∏è Activo') : '‚èπÔ∏è Detenido'}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Controles de oleadas */}
        <div className="admin-section-v2">
          <h3 className="section-title">üåä Control de Oleadas</h3>
          <div className="wave-controls">
            <button 
              className="admin-button-v2 wave-start" 
              onClick={startWaves}
              disabled={!isConnected || (gameState.waveSystem.isActive && !gameState.waveSystem.isPaused)}
            >
              {gameState.waveSystem.isActive && gameState.waveSystem.isPaused ? '‚ñ∂Ô∏è Reanudar' : 'üöÄ Iniciar'}
            </button>
            <button 
              className="admin-button-v2 wave-pause" 
              onClick={pauseWaves}
              disabled={!isConnected || !gameState.waveSystem.isActive || gameState.waveSystem.isPaused}
            >
              ‚è∏Ô∏è Pausar
            </button>
            <button 
              className="admin-button-v2 wave-stop" 
              onClick={stopWaves}
              disabled={!isConnected}
            >
              üõë Detener & Reset
            </button>
          </div>
        </div>
        
        {/* Estado de jugadores */}
        <div className="admin-section-v2">
          <h3 className="section-title">üë• Estado de Jugadores</h3>
          <div className="players-grid">
            {gameState.lanes.map((lane, index) => (
              <div 
                key={lane.id} 
                className={`player-card ${lane.isGameOver ? 'eliminated' : 'alive'}`}
              >
                <div className="player-header">
                  <h4>Jugador {index + 1}</h4>
                  <div className={`player-status ${lane.isGameOver ? 'dead' : 'alive'}`}>
                    {lane.isGameOver ? 'üíÄ' : '‚ù§Ô∏è'}
                  </div>
                </div>
                <div className="player-stats">
                  <div className="stat-row">
                    <span>‚ù§Ô∏è Vida:</span>
                    <span className={`health-value ${
                      lane.baseHealth > 66 ? 'high' : 
                      lane.baseHealth > 33 ? 'medium' : 'low'
                    }`}>
                      {lane.baseHealth}/100
                    </span>
                  </div>
                  <div className="stat-row">
                    <span>üëæ Enemigos:</span>
                    <span>{lane.enemies.length}</span>
                  </div>
                  <div className="stat-row">
                    <span>üî´ Balas:</span>
                    <span>{lane.bullets.length}</span>
                  </div>
                </div>
                <div className="health-bar">
                  <div 
                    className="health-fill"
                    style={{ 
                      width: `${lane.baseHealth}%`,
                      backgroundColor: lane.baseHealth > 66 ? '#00ff88' : 
                                     lane.baseHealth > 33 ? '#ffaa00' : '#ff4444'
                    }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Controles manuales */}
        <div className="admin-section-v2">
          <h3 className="section-title">üëæ Spawn Manual de Enemigos</h3>
          <div className="manual-controls">
            {gameState.lanes.map((lane, index) => (
              <button 
                key={lane.id}
                className={`admin-button-v2 spawn-enemy ${lane.isGameOver ? 'disabled' : ''}`}
                onClick={() => spawnEnemy(lane.id)}
                disabled={!isConnected || lane.isGameOver}
              >
                {lane.isGameOver ? 'üíÄ' : 'üëæ'} P{index + 1}
              </button>
            ))}
          </div>
        </div>
        
        {/* Controles de disparos */}
        <div className="admin-section-v2">
          <h3 className="section-title">üî´ Control de Disparos</h3>
          <div className="shoot-controls">
            <div className="individual-shoots">
              {gameState.lanes.map((lane, index) => (
                <button 
                  key={lane.id}
                  className={`admin-button-v2 shoot-bullet ${lane.isGameOver ? 'disabled' : ''}`}
                  onClick={() => shootBullet(lane.id)}
                  disabled={!isConnected || lane.isGameOver}
                >
                  üî´ P{index + 1}
                </button>
              ))}
            </div>
            <button 
              className="admin-button-v2 shoot-all" 
              onClick={shootAllBullets}
              disabled={!isConnected}
            >
              üöÄ Disparar Todos
            </button>
          </div>
        </div>

        {/* Panel de ganador */}
        {gameState.globalGameOver && (
          <div className="winner-panel">
            {gameState.winner ? (
              <>
                <h2 className="winner-announcement">üèÜ ¬°TENEMOS UN GANADOR!</h2>
                <div className="winner-details">
                  <h3>Jugador {gameState.winner} es el Campe√≥n</h3>
                  <p>Puntuaci√≥n Final: {gameState.totalScore}</p>
                  <p>Oleadas Completadas: {gameState.waveSystem.currentWave}</p>
                </div>
              </>
            ) : (
              <>
                <h2 className="winner-announcement">üíÄ DERROTA TOTAL</h2>
                <div className="winner-details">
                  <h3>Todos los jugadores han sido eliminados</h3>
                  <p>Puntuaci√≥n Final: {gameState.totalScore}</p>
                  <p>Oleadas Completadas: {gameState.waveSystem.currentWave}</p>
                </div>
              </>
            )}
          </div>
        )}
        
        <div className="admin-footer">
          <p>üîó <a 
            href="/game" 
            className="game-link"
            target="_blank"
            rel="noopener noreferrer"
          >
            Ver Pantalla de Juego
          </a></p>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
