import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';

const GamePage = () => {
  const canvasRef = useRef(null);
  const socketRef = useRef(null);
  const [gameState, setGameState] = useState({
    lanes: [
      { id: 1, baseHealth: 100, enemies: [], bullets: [], enemyProjectiles: [], isGameOver: false },
      { id: 2, baseHealth: 100, enemies: [], bullets: [], enemyProjectiles: [], isGameOver: false },
      { id: 3, baseHealth: 100, enemies: [], bullets: [], enemyProjectiles: [], isGameOver: false },
      { id: 4, baseHealth: 100, enemies: [], bullets: [], enemyProjectiles: [], isGameOver: false }
    ],
    waveSystem: {
      isActive: false,
      isPaused: false,
      currentWave: 0,
      timeRemaining: 15,
      maxTime: 15
    },
    globalGameOver: false,
    winner: null,
    enemyTypes: {}
  });
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    socketRef.current = io('http://localhost:5000');
    
    socketRef.current.on('connect', () => {
      setIsConnected(true);
      console.log('Conectado al servidor');
    });
    
    socketRef.current.on('disconnect', () => {
      setIsConnected(false);
      console.log('Desconectado del servidor');
    });
    
    socketRef.current.on('gameState', (newGameState) => {
      setGameState(newGameState);
    });
    
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const drawGame = () => {
      // Limpiar canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Dibujar fondo con gradiente
      const gradient = ctx.createLinearGradient(0, 0, 0, canvas.height);
      gradient.addColorStop(0, '#0a0a0a');
      gradient.addColorStop(0.3, '#1a1a2e');
      gradient.addColorStop(0.7, '#16213e');
      gradient.addColorStop(1, '#0f3460');
      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      
      const laneWidth = canvas.width / 4;
      
      // Dibujar carriles
      gameState.lanes.forEach((lane, laneIndex) => {
        const x = laneIndex * laneWidth;
        const laneX = x + laneWidth / 2;
        
        // Fondo del carril
        if (lane.isGameOver) {
          ctx.fillStyle = 'rgba(231, 76, 60, 0.15)';
          ctx.fillRect(x, 0, laneWidth, canvas.height);
        }
        
        // L√≠neas separadoras con efecto ne√≥n
        ctx.strokeStyle = lane.isGameOver ? '#e74c3c' : '#00d4ff';
        ctx.lineWidth = 2;
        ctx.shadowColor = lane.isGameOver ? '#e74c3c' : '#00d4ff';
        ctx.shadowBlur = 8;
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.stroke();
        ctx.shadowBlur = 0;
        
        // N√∫meros de carril
        ctx.fillStyle = lane.isGameOver ? '#e74c3c' : '#00ff88';
        ctx.font = 'bold 20px "Courier New"';
        ctx.textAlign = 'center';
        ctx.shadowColor = ctx.fillStyle;
        ctx.shadowBlur = 6;
        ctx.fillText(`P${laneIndex + 1}`, laneX, 30);
        ctx.shadowBlur = 0;
        
        // Vida de la base con barra visual
        const baseY = canvas.height - 60;
        const barWidth = laneWidth - 20;
        const barHeight = 12;
        const barX = x + 10;
        
        // Fondo de la barra
        ctx.fillStyle = 'rgba(0, 0, 0, 0.6)';
        ctx.fillRect(barX, baseY, barWidth, barHeight);
        
        // Barra de vida
        const healthPercent = lane.baseHealth / 100;
        const healthWidth = barWidth * healthPercent;
        const healthColor = healthPercent > 0.6 ? '#00ff88' : 
                           healthPercent > 0.3 ? '#ffaa00' : '#ff4444';
        
        ctx.fillStyle = healthColor;
        ctx.fillRect(barX, baseY, healthWidth, barHeight);
        
        // Texto de vida
        ctx.fillStyle = '#ffffff';
        ctx.font = 'bold 12px Arial';
        ctx.fillText(`${lane.baseHealth}/100`, laneX, baseY + 25);
        
        // Game Over overlay para carril
        if (lane.isGameOver) {
          ctx.fillStyle = 'rgba(231, 76, 60, 0.8)';
          ctx.fillRect(x, 0, laneWidth, canvas.height);
          
          ctx.fillStyle = '#ffffff';
          ctx.font = 'bold 16px Arial';
          ctx.textAlign = 'center';
          ctx.shadowColor = '#000000';
          ctx.shadowBlur = 5;
          ctx.save();
          ctx.translate(laneX, canvas.height / 2);
          ctx.rotate(-Math.PI / 2);
          ctx.fillText('ELIMINADO', 0, 0);
          ctx.restore();
          ctx.shadowBlur = 0;
        }
        
        // Dibujar enemigos con diferentes estilos por tipo
        if (lane.enemies && Array.isArray(lane.enemies)) {
          lane.enemies.forEach(enemy => {
            if (enemy.alive) {
              const enemyType = gameState.enemyTypes[enemy.type?.toUpperCase()] || 
                              { color: '#e74c3c', size: 15 };
              
              // Sombra del enemigo
              ctx.fillStyle = 'rgba(0, 0, 0, 0.3)';
              ctx.beginPath();
              ctx.arc(laneX + 2, enemy.y + 2, enemyType.size + 2, 0, Math.PI * 2);
              ctx.fill();
              
              // Cuerpo del enemigo con color espec√≠fico
              ctx.fillStyle = enemyType.color;
              ctx.shadowColor = enemyType.color;
              ctx.shadowBlur = 5;
              ctx.beginPath();
              
              // Formas espec√≠ficas por tipo
              if (enemy.type === 'tank') {
                // Tanque - rect√°ngulo
                ctx.fillRect(laneX - enemyType.size/2, enemy.y - enemyType.size/2, 
                            enemyType.size, enemyType.size);
              } else if (enemy.type === 'mini') {
                // Mini - tri√°ngulo peque√±o
                ctx.beginPath();
                ctx.moveTo(laneX, enemy.y - enemyType.size/2);
                ctx.lineTo(laneX - enemyType.size/2, enemy.y + enemyType.size/2);
                ctx.lineTo(laneX + enemyType.size/2, enemy.y + enemyType.size/2);
                ctx.closePath();
                ctx.fill();
              } else if (enemy.type === 'sniper') {
                // Sniper - diamante
                ctx.beginPath();
                ctx.moveTo(laneX, enemy.y - enemyType.size/2);
                ctx.lineTo(laneX + enemyType.size/2, enemy.y);
                ctx.lineTo(laneX, enemy.y + enemyType.size/2);
                ctx.lineTo(laneX - enemyType.size/2, enemy.y);
                ctx.closePath();
                ctx.fill();
              } else if (enemy.type === 'boss') {
                // Boss - estrella
                ctx.beginPath();
                for (let i = 0; i < 8; i++) {
                  const angle = (i * Math.PI) / 4;
                  const radius = i % 2 === 0 ? enemyType.size : enemyType.size / 2;
                  const px = laneX + Math.cos(angle) * radius;
                  const py = enemy.y + Math.sin(angle) * radius;
                  if (i === 0) ctx.moveTo(px, py);
                  else ctx.lineTo(px, py);
                }
                ctx.closePath();
                ctx.fill();
              } else {
                // B√°sico y Especial 1 - c√≠rculo
                ctx.arc(laneX, enemy.y, enemyType.size, 0, Math.PI * 2);
                ctx.fill();
              }
              
              ctx.shadowBlur = 0;
              
              // Borde del enemigo
              ctx.strokeStyle = '#ffffff';
              ctx.lineWidth = 1;
              ctx.stroke();
              
              // Barra de vida del enemigo (solo si no est√° a m√°xima vida)
              if (enemy.health < enemy.maxHealth) {
                const enemyHealthPercent = enemy.health / enemy.maxHealth;
                const healthBarWidth = enemyType.size * 1.5;
                const healthBarHeight = 3;
                const healthBarX = laneX - healthBarWidth / 2;
                const healthBarY = enemy.y - enemyType.size - 8;
                
                // Fondo de barra de vida
                ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
                ctx.fillRect(healthBarX, healthBarY, healthBarWidth, healthBarHeight);
                
                // Barra de vida del enemigo
                ctx.fillStyle = enemyHealthPercent > 0.5 ? '#00ff00' : '#ff4444';
                ctx.fillRect(healthBarX, healthBarY, healthBarWidth * enemyHealthPercent, healthBarHeight);
              }
              
              // Vida num√©rica solo para enemigos grandes
              if (enemyType.size >= 20 || enemy.type === 'tank') {
                ctx.fillStyle = '#ffffff';
                ctx.font = 'bold 10px Arial';
                ctx.textAlign = 'center';
                ctx.shadowColor = '#000000';
                ctx.shadowBlur = 2;
                ctx.fillText(enemy.health, laneX, enemy.y + 3);
                ctx.shadowBlur = 0;
              }
            }
          });
        }
        
        // Dibujar proyectiles de enemigos (sniper)
        if (lane.enemyProjectiles && Array.isArray(lane.enemyProjectiles)) {
          lane.enemyProjectiles.forEach(projectile => {
            if (projectile.alive) {
              // Proyectil del sniper - rojo
              ctx.fillStyle = '#ff0066';
              ctx.shadowColor = '#ff0066';
              ctx.shadowBlur = 10;
              ctx.beginPath();
              ctx.arc(laneX, projectile.y, 4, 0, Math.PI * 2);
              ctx.fill();
              
              // N√∫cleo brillante
              ctx.fillStyle = '#ffffff';
              ctx.beginPath();
              ctx.arc(laneX - 1, projectile.y - 1, 2, 0, Math.PI * 2);
              ctx.fill();
              ctx.shadowBlur = 0;
            }
          });
        }
        
        // Dibujar balas de defensa
        if (lane.bullets && Array.isArray(lane.bullets)) {
          lane.bullets.forEach(bullet => {
            if (bullet.alive) {
              // Efecto de trail
              ctx.fillStyle = 'rgba(255, 215, 0, 0.3)';
              ctx.beginPath();
              ctx.arc(laneX, bullet.y + 8, 6, 0, Math.PI * 2);
              ctx.fill();
              
              // Bala principal
              ctx.fillStyle = '#ffd700';
              ctx.shadowColor = '#ffd700';
              ctx.shadowBlur = 12;
              ctx.beginPath();
              ctx.arc(laneX, bullet.y, 5, 0, Math.PI * 2);
              ctx.fill();
              
              // N√∫cleo brillante
              ctx.fillStyle = '#ffffff';
              ctx.beginPath();
              ctx.arc(laneX - 2, bullet.y - 2, 2, 0, Math.PI * 2);
              ctx.fill();
              ctx.shadowBlur = 0;
            }
          });
        }
      });
      
      // L√≠nea de las bases con efecto ne√≥n
      ctx.strokeStyle = '#00ff88';
      ctx.lineWidth = 4;
      ctx.shadowColor = '#00ff88';
      ctx.shadowBlur = 15;
      ctx.beginPath();
      ctx.moveTo(0, canvas.height - 80);
      ctx.lineTo(canvas.width, canvas.height - 80);
      ctx.stroke();
      ctx.shadowBlur = 0;
    };

    drawGame();
  }, [gameState]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getEnemyCount = () => {
    return gameState.lanes.reduce((total, lane) => {
      return total + (lane.enemies ? lane.enemies.length : 0);
    }, 0);
  };

  const getActiveProjectiles = () => {
    return gameState.lanes.reduce((total, lane) => {
      return total + (lane.enemyProjectiles ? lane.enemyProjectiles.length : 0);
    }, 0);
  };

  return (
    <div className="game-container">
      {/* Panel de informaci√≥n superior */}
      <div className="game-info-panel">
        <div className="info-section">
          <h3>üåä Oleada {gameState.waveSystem.currentWave}</h3>
          <p>Tiempo: {formatTime(gameState.waveSystem.timeRemaining)}</p>
        </div>
        <div className="info-section">
          <h3>üëæ Enemigos Activos</h3>
          <p>{getEnemyCount()}</p>
        </div>
        <div className="info-section">
          <h3>üéØ Proyectiles</h3>
          <p>{getActiveProjectiles()}</p>
        </div>
        <div className="info-section">
          <h3>üì° Estado</h3>
          <p style={{ color: isConnected ? '#00ff88' : '#ff4444' }}>
            {isConnected ? 'üü¢ Conectado' : 'üî¥ Desconectado'}
          </p>
        </div>
        <div className="info-section">
          <h3>‚öîÔ∏è Estado del Juego</h3>
          <p style={{ 
            color: gameState.waveSystem.isActive ? 
              (gameState.waveSystem.isPaused ? '#ffaa00' : '#00ff88') : '#888888' 
          }}>
            {gameState.waveSystem.isActive ? 
              (gameState.waveSystem.isPaused ? '‚è∏Ô∏è Pausado' : '‚ñ∂Ô∏è Activo') : '‚èπÔ∏è Detenido'}
          </p>
        </div>
      </div>
      
      {/* Canvas del juego */}
      <div className="game-canvas-container">
        <canvas
          ref={canvasRef}
          width={400}
          height={600}
          className="game-canvas-modern"
        />
        
        {/* Overlay de ganador */}
        {gameState.globalGameOver && (
          <div className="winner-overlay">
            {gameState.winner ? (
              <>
                <h1 className="winner-title">üèÜ ¬°VICTORIA!</h1>
                <h2 className="winner-subtitle">Jugador {gameState.winner} Sobrevive</h2>
                <p className="winner-score">Oleadas Completadas: {gameState.waveSystem.currentWave}</p>
              </>
            ) : (
              <>
                <h1 className="winner-title">üíÄ ANIQUILACI√ìN TOTAL</h1>
                <h2 className="winner-subtitle">Todos los defensores han ca√≠do</h2>
                <p className="winner-score">Oleadas Resistidas: {gameState.waveSystem.currentWave}</p>
              </>
            )}
          </div>
        )}
      </div>
      
      {/* Informaci√≥n de carriles */}
      <div className="lanes-status">
        {gameState.lanes.map((lane, index) => (
          <div 
            key={lane.id} 
            className={`lane-status ${lane.isGameOver ? 'game-over' : 'alive'}`}
          >
            <h4>Jugador {index + 1}</h4>
            <div className="lane-stats">
              <span>‚ù§Ô∏è {lane.baseHealth}/100</span>
              <span>üëæ {lane.enemies ? lane.enemies.length : 0}</span>
              <span>üî´ {lane.bullets ? lane.bullets.length : 0}</span>
              <span>üéØ {lane.enemyProjectiles ? lane.enemyProjectiles.length : 0}</span>
            </div>
            {lane.isGameOver && <div className="status-indicator">üíÄ ELIMINADO</div>}
          </div>
        ))}
      </div>
      
      <div className="game-footer">
        <p>üéÆ Usa el panel de administraci√≥n para controlar el juego</p>
        <p>üîó <a 
          href="/admin" 
          style={{ color: '#00d4ff', textDecoration: 'underline' }}
          target="_blank"
          rel="noopener noreferrer"
        >
          Abrir Panel de Admin
        </a></p>
      </div>
    </div>
  );
};

export default GamePage;
