import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';

const AdminPage = () => {
  const socketRef = useRef(null);
  const [gameState, setGameState] = useState({
    baseHealth: 100,
    lanes: [
      { id: 1, enemies: [], bullets: [] },
      { id: 2, enemies: [], bullets: [] },
      { id: 3, enemies: [], bullets: [] },
      { id: 4, enemies: [], bullets: [] }
    ],
    isGameOver: false,
    score: 0
  });
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Conectar al servidor
    socketRef.current = io(process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000');
    
    socketRef.current.on('connect', () => {
      setIsConnected(true);
      console.log('Admin conectado al servidor');
    });
    
    socketRef.current.on('disconnect', () => {
      setIsConnected(false);
      console.log('Admin desconectado del servidor');
    });
    
    socketRef.current.on('gameState', (newGameState) => {
      setGameState(newGameState);
    });
    
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  const spawnEnemy = (laneId) => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('spawnEnemy', laneId);
    }
  };

  const shootBullet = (laneId) => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('shootBullet', laneId);
    }
  };

  const shootAllBullets = () => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('shootAllBullets');
    }
  };

  const resetGame = () => {
    if (socketRef.current && isConnected) {
      socketRef.current.emit('resetGame');
    }
  };

  return (
    <div className="admin-container">
      <div className="admin-panel">
        <h1 className="admin-title">üéÆ Panel de Control</h1>
        
        {/* Estado de conexi√≥n */}
        <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
          {isConnected ? 'üü¢ Conectado al servidor' : 'üî¥ Desconectado del servidor'}
        </div>
        
        {/* Secci√≥n de enemigos */}
        <div className="admin-section">
          <h3>üëæ Spawn Enemigos</h3>
          <div className="button-group">
            <button 
              className="admin-button enemy" 
              onClick={() => spawnEnemy(1)}
              disabled={!isConnected}
            >
              Carril 1
            </button>
            <button 
              className="admin-button enemy" 
              onClick={() => spawnEnemy(2)}
              disabled={!isConnected}
            >
              Carril 2
            </button>
            <button 
              className="admin-button enemy" 
              onClick={() => spawnEnemy(3)}
              disabled={!isConnected}
            >
              Carril 3
            </button>
            <button 
              className="admin-button enemy" 
              onClick={() => spawnEnemy(4)}
              disabled={!isConnected}
            >
              Carril 4
            </button>
          </div>
        </div>
        
        {/* Secci√≥n de disparos */}
        <div className="admin-section">
          <h3>üî´ Disparar Balas</h3>
          <div className="button-group">
            <button 
              className="admin-button shoot" 
              onClick={() => shootBullet(1)}
              disabled={!isConnected}
            >
              Disparar C1
            </button>
            <button 
              className="admin-button shoot" 
              onClick={() => shootBullet(2)}
              disabled={!isConnected}
            >
              Disparar C2
            </button>
            <button 
              className="admin-button shoot" 
              onClick={() => shootBullet(3)}
              disabled={!isConnected}
            >
              Disparar C3
            </button>
            <button 
              className="admin-button shoot" 
              onClick={() => shootBullet(4)}
              disabled={!isConnected}
            >
              Disparar C4
            </button>
          </div>
          
          <div className="button-group">
            <button 
              className="admin-button special" 
              onClick={shootAllBullets}
              disabled={!isConnected}
            >
              üöÄ Disparar Todos
            </button>
          </div>
        </div>
        
        {/* Secci√≥n de control */}
        <div className="admin-section">
          <h3>‚öôÔ∏è Control del Juego</h3>
          <div className="button-group">
            <button 
              className="admin-button reset" 
              onClick={resetGame}
              disabled={!isConnected}
            >
              üîÑ Reiniciar Juego
            </button>
          </div>
        </div>
        
        {/* Estado del juego */}
        <div className="status-display">
          <h3 style={{ color: 'white', marginBottom: '15px' }}>üìä Estado del Juego</h3>
          
          <div className="status-item">
            <span>‚ù§Ô∏è Vida de la Base:</span>
            <span className="status-value" style={{ color: gameState.baseHealth > 50 ? '#27ae60' : '#e74c3c' }}>
              {gameState.baseHealth}/100
            </span>
          </div>
          
          <div className="status-item">
            <span>‚≠ê Puntuaci√≥n:</span>
            <span className="status-value" style={{ color: '#f39c12' }}>
              {gameState.score}
            </span>
          </div>
          
          <div className="status-item">
            <span>üéÆ Estado:</span>
            <span className="status-value" style={{ color: gameState.isGameOver ? '#e74c3c' : '#27ae60' }}>
              {gameState.isGameOver ? 'Game Over' : 'Jugando'}
            </span>
          </div>
          
          {/* Estado de carriles */}
          <div style={{ marginTop: '15px' }}>
            <h4 style={{ color: 'white', marginBottom: '10px' }}>üõ£Ô∏è Estado de Carriles:</h4>
            {gameState.lanes.map((lane, index) => (
              <div key={lane.id} className="status-item">
                <span>Carril {index + 1}:</span>
                <span className="status-value">
                  üëæ {lane.enemies.length} | üî´ {lane.bullets.length}
                </span>
              </div>
            ))}
          </div>
        </div>
        
        <div style={{ 
          marginTop: '20px', 
          textAlign: 'center',
          color: 'white',
          fontSize: '16px'
        }}>
          <p>üîó <a 
            href="/game" 
            style={{ color: '#3498db', textDecoration: 'underline' }}
            target="_blank"
          >
            Ver Juego en Pantalla Completa
          </a></p>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
