import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';

const GamePage = () => {
  const canvasRef = useRef(null);
  const socketRef = useRef(null);
  const [gameState, setGameState] = useState({
    baseHealth: 100,
    lanes: [
      { id: 1, enemies: [], bullets: [] },
      { id: 2, enemies: [], bullets: [] },
      { id: 3, enemies: [], bullets: [] },
      { id: 4, enemies: [], bullets: [] }
    ],
    isGameOver: false,
    score: 0
  });
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Conectar al servidor
    socketRef.current = io('http://localhost:5000');
    
    socketRef.current.on('connect', () => {
      setIsConnected(true);
      console.log('Conectado al servidor');
    });
    
    socketRef.current.on('disconnect', () => {
      setIsConnected(false);
      console.log('Desconectado del servidor');
    });
    
    socketRef.current.on('gameState', (newGameState) => {
      setGameState(newGameState);
    });
    
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const drawGame = () => {
      // Limpiar canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Dibujar fondo de carriles
      const laneWidth = canvas.width / 4;
      for (let i = 0; i < 4; i++) {
        const x = i * laneWidth;
        
        // Líneas separadoras
        ctx.strokeStyle = '#3498db';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.stroke();
        
        // Números de carril
        ctx.fillStyle = '#ecf0f1';
        ctx.font = '20px Arial';
        ctx.textAlign = 'center';
        ctx.fillText(`${i + 1}`, x + laneWidth / 2, 30);
      }
      
      // Dibujar línea de la base
      ctx.strokeStyle = '#e74c3c';
      ctx.lineWidth = 4;
      ctx.beginPath();
      ctx.moveTo(0, canvas.height - 20);
      ctx.lineTo(canvas.width, canvas.height - 20);
      ctx.stroke();
      
      // Dibujar enemigos
      gameState.lanes.forEach((lane, laneIndex) => {
        const laneX = laneIndex * laneWidth + laneWidth / 2;
        
        lane.enemies.forEach(enemy => {
          if (enemy.alive) {
            // Cuerpo del enemigo
            ctx.fillStyle = '#e74c3c';
            ctx.beginPath();
            ctx.arc(laneX, enemy.y, 15, 0, Math.PI * 2);
            ctx.fill();
            
            // Borde del enemigo
            ctx.strokeStyle = '#c0392b';
            ctx.lineWidth = 2;
            ctx.stroke();
            
            // Mostrar vida del enemigo
            ctx.fillStyle = '#ffffff';
            ctx.font = '12px Arial';
            ctx.textAlign = 'center';
            ctx.fillText(enemy.health, laneX, enemy.y + 4);
          }
        });
        
        // Dibujar balas
        lane.bullets.forEach(bullet => {
          if (bullet.alive) {
            ctx.fillStyle = '#f39c12';
            ctx.beginPath();
            ctx.arc(laneX, bullet.y, 5, 0, Math.PI * 2);
            ctx.fill();
            
            // Efecto de brillo
            ctx.fillStyle = '#fff';
            ctx.beginPath();
            ctx.arc(laneX - 2, bullet.y - 2, 2, 0, Math.PI * 2);
            ctx.fill();
          }
        });
      });
    };

    drawGame();
  }, [gameState]);

  const resetGame = () => {
    if (socketRef.current) {
      socketRef.current.emit('resetGame');
    }
  };

  return (
    <div className="game-container">
      <div className="game-canvas" style={{ position: 'relative' }}>
        <canvas
          ref={canvasRef}
          width={400}
          height={600}
          style={{
            background: 'linear-gradient(180deg, #2c3e50 0%, #34495e 100%)',
            borderRadius: '20px',
            display: 'block'
          }}
        />
        
        {/* UI del juego */}
        <div className="game-ui">
          <div className="health-display">
            ❤️ Base: {gameState.baseHealth}/100
          </div>
          <div className="score-display">
            ⭐ Score: {gameState.score}
          </div>
          <div style={{ 
            color: isConnected ? '#27ae60' : '#e74c3c',
            fontSize: '14px'
          }}>
            {isConnected ? '🟢 Conectado' : '🔴 Desconectado'}
          </div>
        </div>
        
        {/* Overlay de Game Over */}
        {gameState.isGameOver && (
          <div className="game-over-overlay">
            <h2>¡GAME OVER!</h2>
            <p>Score Final: {gameState.score}</p>
            <button onClick={resetGame}>
              Reiniciar Juego
            </button>
          </div>
        )}
      </div>
      
      <div style={{ 
        marginTop: '20px', 
        textAlign: 'center',
        color: 'white',
        fontSize: '18px',
        textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
      }}>
        <p>🎮 Usa el panel de administración para controlar el juego</p>
        <p>🔗 <a 
          href="/admin" 
          style={{ color: '#3498db', textDecoration: 'underline' }}
          target="_blank"
        >
          Abrir Panel de Admin
        </a></p>
      </div>
    </div>
  );
};

export default GamePage;
