import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';

const GamePage = () => {
  const canvasRef = useRef(null);
  const socketRef = useRef(null);
  const [gameState, setGameState] = useState({
    lanes: [
      { id: 1, baseHealth: 100, enemies: [], bullets: [], isGameOver: false },
      { id: 2, baseHealth: 100, enemies: [], bullets: [], isGameOver: false },
      { id: 3, baseHealth: 100, enemies: [], bullets: [], isGameOver: false },
      { id: 4, baseHealth: 100, enemies: [], bullets: [], isGameOver: false }
    ],
    waveSystem: {
      isActive: false,
      isPaused: false,
      currentWave: 0,
      timeRemaining: 20
    },
    globalGameOver: false,
    winner: null,
    totalScore: 0
  });
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    socketRef.current = io('http://localhost:5000');
    
    socketRef.current.on('connect', () => {
      setIsConnected(true);
      console.log('Conectado al servidor');
    });
    
    socketRef.current.on('disconnect', () => {
      setIsConnected(false);
      console.log('Desconectado del servidor');
    });
    
    socketRef.current.on('gameState', (newGameState) => {
      setGameState(newGameState);
    });
    
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const drawGame = () => {
      // Limpiar canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Dibujar fondo con gradiente
      const gradient = ctx.createLinearGradient(0, 0, 0, canvas.height);
      gradient.addColorStop(0, '#1a1a2e');
      gradient.addColorStop(0.5, '#16213e');
      gradient.addColorStop(1, '#0f3460');
      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      
      const laneWidth = canvas.width / 4;
      
      // Dibujar carriles
      gameState.lanes.forEach((lane, laneIndex) => {
        const x = laneIndex * laneWidth;
        const laneX = x + laneWidth / 2;
        
        // Fondo del carril
        if (lane.isGameOver) {
          // Carril muerto - fondo rojo oscuro
          ctx.fillStyle = 'rgba(231, 76, 60, 0.2)';
          ctx.fillRect(x, 0, laneWidth, canvas.height);
        }
        
        // L铆neas separadoras con efecto ne贸n
        ctx.strokeStyle = lane.isGameOver ? '#e74c3c' : '#00d4ff';
        ctx.lineWidth = 3;
        ctx.shadowColor = lane.isGameOver ? '#e74c3c' : '#00d4ff';
        ctx.shadowBlur = 10;
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.stroke();
        ctx.shadowBlur = 0;
        
        // N煤meros de carril con estilo futurista
        ctx.fillStyle = lane.isGameOver ? '#e74c3c' : '#00ff88';
        ctx.font = 'bold 24px "Courier New"';
        ctx.textAlign = 'center';
        ctx.shadowColor = ctx.fillStyle;
        ctx.shadowBlur = 8;
        ctx.fillText(`P${laneIndex + 1}`, laneX, 35);
        ctx.shadowBlur = 0;
        
        // Vida de la base con barra visual
        const baseY = canvas.height - 60;
        const barWidth = laneWidth - 20;
        const barHeight = 12;
        const barX = x + 10;
        
        // Fondo de la barra
        ctx.fillStyle = 'rgba(0, 0, 0, 0.5)';
        ctx.fillRect(barX, baseY, barWidth, barHeight);
        
        // Barra de vida
        const healthPercent = lane.baseHealth / 100;
        const healthWidth = barWidth * healthPercent;
        const healthColor = healthPercent > 0.6 ? '#00ff88' : 
                           healthPercent > 0.3 ? '#ffaa00' : '#ff4444';
        
        ctx.fillStyle = healthColor;
        ctx.fillRect(barX, baseY, healthWidth, barHeight);
        
        // Texto de vida
        ctx.fillStyle = '#ffffff';
        ctx.font = 'bold 14px Arial';
        ctx.fillText(`${lane.baseHealth}/100`, laneX, baseY + 25);
        
        // Game Over overlay para carril
        if (lane.isGameOver) {
          ctx.fillStyle = 'rgba(231, 76, 60, 0.8)';
          ctx.fillRect(x, 0, laneWidth, canvas.height);
          
          // Texto GAME OVER
          ctx.fillStyle = '#ffffff';
          ctx.font = 'bold 20px Arial';
          ctx.textAlign = 'center';
          ctx.shadowColor = '#000000';
          ctx.shadowBlur = 5;
          ctx.save();
          ctx.translate(laneX, canvas.height / 2);
          ctx.rotate(-Math.PI / 2);
          ctx.fillText('GAME OVER', 0, 0);
          ctx.restore();
          ctx.shadowBlur = 0;
        }
        
        // Dibujar enemigos con efectos
        lane.enemies.forEach(enemy => {
          if (enemy.alive) {
            // Sombra del enemigo
            ctx.fillStyle = 'rgba(0, 0, 0, 0.3)';
            ctx.beginPath();
            ctx.arc(laneX + 2, enemy.y + 2, 16, 0, Math.PI * 2);
            ctx.fill();
            
            // Cuerpo del enemigo con gradiente
            const enemyGradient = ctx.createRadialGradient(laneX, enemy.y, 0, laneX, enemy.y, 15);
            enemyGradient.addColorStop(0, '#ff6b6b');
            enemyGradient.addColorStop(1, '#ee5a52');
            ctx.fillStyle = enemyGradient;
            ctx.beginPath();
            ctx.arc(laneX, enemy.y, 15, 0, Math.PI * 2);
            ctx.fill();
            
            // Borde del enemigo
            ctx.strokeStyle = '#c0392b';
            ctx.lineWidth = 2;
            ctx.stroke();
            
            // Barra de vida del enemigo
            const enemyHealthPercent = enemy.health / enemy.maxHealth;
            const healthBarWidth = 20;
            const healthBarHeight = 4;
            const healthBarX = laneX - healthBarWidth / 2;
            const healthBarY = enemy.y - 25;
            
            // Fondo de barra de vida
            ctx.fillStyle = 'rgba(0, 0, 0, 0.5)';
            ctx.fillRect(healthBarX, healthBarY, healthBarWidth, healthBarHeight);
            
            // Barra de vida del enemigo
            ctx.fillStyle = enemyHealthPercent > 0.5 ? '#00ff00' : '#ff4444';
            ctx.fillRect(healthBarX, healthBarY, healthBarWidth * enemyHealthPercent, healthBarHeight);
            
            // Vida num茅rica
            ctx.fillStyle = '#ffffff';
            ctx.font = '10px Arial';
            ctx.textAlign = 'center';
            ctx.fillText(enemy.health, laneX, enemy.y + 4);
          }
        });
        
        // Dibujar balas con efectos
        lane.bullets.forEach(bullet => {
          if (bullet.alive) {
            // Efecto de trail
            ctx.fillStyle = 'rgba(255, 215, 0, 0.3)';
            ctx.beginPath();
            ctx.arc(laneX, bullet.y + 10, 8, 0, Math.PI * 2);
            ctx.fill();
            
            // Bala principal
            ctx.fillStyle = '#ffd700';
            ctx.shadowColor = '#ffd700';
            ctx.shadowBlur = 15;
            ctx.beginPath();
            ctx.arc(laneX, bullet.y, 6, 0, Math.PI * 2);
            ctx.fill();
            
            // N煤cleo brillante
            ctx.fillStyle = '#ffffff';
            ctx.beginPath();
            ctx.arc(laneX - 2, bullet.y - 2, 3, 0, Math.PI * 2);
            ctx.fill();
            ctx.shadowBlur = 0;
          }
        });
      });
      
      // L铆nea de las bases con efecto ne贸n
      ctx.strokeStyle = '#00ff88';
      ctx.lineWidth = 4;
      ctx.shadowColor = '#00ff88';
      ctx.shadowBlur = 15;
      ctx.beginPath();
      ctx.moveTo(0, canvas.height - 80);
      ctx.lineTo(canvas.width, canvas.height - 80);
      ctx.stroke();
      ctx.shadowBlur = 0;
    };

    drawGame();
  }, [gameState]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="game-container">
      {/* Panel de informaci贸n superior */}
      <div className="game-info-panel">
        <div className="info-section">
          <h3> Oleada {gameState.waveSystem.currentWave}</h3>
          <p>Tiempo: {formatTime(gameState.waveSystem.timeRemaining)}</p>
        </div>
        <div className="info-section">
          <h3> Puntuaci贸n Total</h3>
          <p>{gameState.totalScore}</p>
        </div>
        <div className="info-section">
          <h3> Estado</h3>
          <p style={{ color: isConnected ? '#00ff88' : '#ff4444' }}>
            {isConnected ? ' Conectado' : ' Desconectado'}
          </p>
        </div>
        <div className="info-section">
          <h3>锔 Estado del Juego</h3>
          <p style={{ 
            color: gameState.waveSystem.isActive ? 
              (gameState.waveSystem.isPaused ? '#ffaa00' : '#00ff88') : '#888888' 
          }}>
            {gameState.waveSystem.isActive ? 
              (gameState.waveSystem.isPaused ? '革 Pausado' : '讹 Activo') : '癸 Detenido'}
          </p>
        </div>
      </div>
      
      {/* Canvas del juego */}
      <div className="game-canvas-container">
        <canvas
          ref={canvasRef}
          width={400}
          height={600}
          className="game-canvas-modern"
        />
        
        {/* Overlay de ganador */}
        {gameState.globalGameOver && (
          <div className="winner-overlay">
            {gameState.winner ? (
              <>
                <h1 className="winner-title"> 隆VICTORIA!</h1>
                <h2 className="winner-subtitle">Jugador {gameState.winner} Gana</h2>
                <p className="winner-score">Puntuaci贸n Final: {gameState.totalScore}</p>
              </>
            ) : (
              <>
                <h1 className="winner-title"> DERROTA TOTAL</h1>
                <h2 className="winner-subtitle">Todos los jugadores han ca铆do</h2>
                <p className="winner-score">Puntuaci贸n Final: {gameState.totalScore}</p>
              </>
            )}
          </div>
        )}
      </div>
      
      {/* Informaci贸n de carriles */}
      <div className="lanes-status">
        {gameState.lanes.map((lane, index) => (
          <div 
            key={lane.id} 
            className={`lane-status ${lane.isGameOver ? 'game-over' : 'alive'}`}
          >
            <h4>Jugador {index + 1}</h4>
            <div className="lane-stats">
              <span>わ {lane.baseHealth}/100</span>
              <span> {lane.enemies.length}</span>
              <span> {lane.bullets.length}</span>
            </div>
            {lane.isGameOver && <div className="status-indicator"> ELIMINADO</div>}
          </div>
        ))}
      </div>
      
      <div className="game-footer">
        <p> Usa el panel de administraci贸n para controlar el juego</p>
        <p> <a 
          href="/admin" 
          style={{ color: '#00d4ff', textDecoration: 'underline' }}
          target="_blank"
          rel="noopener noreferrer"
        >
          Abrir Panel de Admin
        </a></p>
      </div>
    </div>
  );
};

export default GamePage;
